  private static @NonNull String makeMessageFor(final @NonNull Attr attr) {
    return attr.description() + " tiene q ser un email valido";
  }

  public enum ForString implements Constraint<@NonNull String> {
    INSTANCE;

    @Override public boolean isInvalid(final @NonNull String value) {
      return !PATTERN.matcher(value).matches();
    }

    @Override public @NonNull String messageFor(final @NonNull Attr attr, final @NonNull String value) {
      return makeMessageFor(attr);
    }

    @Override public @NonNull String getName() {
      return NAME;
    }
  }

  enum ForEmail implements Constraint<@NonNull Email> {
    INSTANCE;

    @Override public boolean isInvalid(final @NonNull Email value) {
      return !PATTERN.matcher(value.getEmail()).matches();
    }

    @Override public @NonNull String messageFor(final @NonNull Attr attr, final @NonNull Email value) {
      return makeMessageFor(attr);
    }

    @Override public @NonNull String getName() {
      return NAME;
    }
  }
