        private static String makeMessageFor(final Attr attr)
        {
                return attr.description() + " tiene q ser un email valido";
        }

        public enum ForString implements Constraint<String> {
                INSTANCE;

                @Override
                public boolean isInvalid(final String value)
                {
                        return !PATTERN.matcher(value).matches();
                }

                @Override
                public String messageFor(final Attr attr, final String value)
                {
                        return makeMessageFor(attr);
                }

                @Override
                public String getName()
                {
                        return NAME;
                }
        }

        enum ForEmail implements Constraint<Email> {
                INSTANCE;

                @Override
                public boolean isInvalid(final Email value)
                {
                        return !PATTERN.matcher(value.getEmail()).matches();
                }

                @Override
                public String messageFor(final Attr attr, final Email value)
                {
                        return makeMessageFor(attr);
                }

                @Override
                public String getName()
                {
                        return NAME;
                }
        }
